mapper.select:
SELECT mpb.guid basisGuid, IFNULL(group_release.total_sign_funds,0) AS groups -- 集团下达 , ( case mpb.plan_status when 0 then 0 else region_release.total_sign_funds end ) AS region, IFNULL(project_release.total_sign_funds,0) project, -- 项目上报 bu.`business_id`, DATE_FORMAT(mpb.`months`, '%Y-%m-%d') months, bu.`business_name` AS businessName, bu.`region_org_id`, ( CASE mpb.`plan_status` WHEN 0 THEN '区域/事业部未下发' WHEN 1 THEN '区域/事业部已下发' WHEN 2 THEN '区域/事业部已上报' WHEN 3 THEN '区域/事业部已推送' ELSE '集团未下达' END ) AS planStatus, mpb.`plan_status` AS plan_status_code, mp.`plan_name` AS planName
,IFNULL( xmsl.cou,0) AS report_project_quantity ,temp.is_manage AS is_manage ,groupstatus.plan_status groupStatus FROM mm_business_unit bu LEFT JOIN `mm_monthly_plan` mp ON 0 = 0 LEFT JOIN `mm_monthly_plan_basis` mpb ON mpb.`months` = mp.`months` AND mpb.`prepared_by_unit_org_id` = bu.`business_id` -- 集团下达数据 LEFT JOIN (
SELECT total_sign_funds, mpi.months, mpi.`prepared_by_unit_org_id` FROM `mm_monthly_plan_index` mpi LEFT JOIN mm_business_unit bu ON bu.`business_id` = mpi.`prepared_by_unit_org_id` LEFT JOIN `mm_monthly_plan_basis` mpb ON mpb.months = mpi.months AND bu.`business_id` = mpi.`prepared_by_unit_org_id` WHERE bu.`type` = 2 AND mpi.prepared_by_unit_type = 1 AND mpb.plan_status IN (1,2) GROUP BY mpi.months, mpi.`prepared_by_unit_org_id`
) group_release ON group_release.months = mp.`months` AND group_release.`prepared_by_unit_org_id` = bu.`business_id` -- 区域下达数据 LEFT JOIN (
SELECT total_sign_funds, mpi.months, mpi.`prepared_by_unit_org_id` FROM `mm_monthly_plan_index` mpi LEFT JOIN mm_business_unit bu ON bu.`business_id` = mpi.`prepared_by_unit_org_id` LEFT JOIN `mm_monthly_plan_basis` mpb ON mpb.months = mpi.months AND bu.`business_id` = mpi.`prepared_by_unit_org_id` WHERE bu.`type` = 2 AND mpi.prepared_by_unit_type = 2 AND mpb.plan_status <>0 GROUP BY mpi.months, mpi.`prepared_by_unit_org_id`
) region_release ON region_release.months = mp.`months` AND region_release.`prepared_by_unit_org_id` = bu.`business_id` -- 项目上报数据 LEFT JOIN (
SELECT total_sign_funds, mpi.months, mpi.`prepared_by_unit_org_id` FROM `mm_monthly_plan_index` mpi LEFT JOIN mm_business_unit bu ON bu.`business_id` = mpi.`prepared_by_unit_org_id` LEFT JOIN `mm_monthly_plan_basis` mpb ON mpb.months = mpi.months AND bu.`business_id` = mpi.`prepared_by_unit_org_id` WHERE bu.`type` = 2 AND mpi.prepared_by_unit_type = 3 AND mpb.plan_status >= 1 GROUP BY mpi.months, mpi.`prepared_by_unit_org_id`
) project_release ON project_release.months = mp.`months` AND project_release.`prepared_by_unit_org_id` = bu.`business_id` -- 授权
LEFT JOIN (
SELECT mpb.plan_status ,mpb.months FROM mm_monthly_plan_basis mpb LEFT JOIN mm_business_unit bu ON bu.`business_id` = mpb.`prepared_by_unit_org_id` WHERE 0=0 AND bu.`type` = 1 GROUP BY mpb.months
) gps ON gps.months = mp.`months`
LEFT JOIN (
SELECT COUNT(*) cou,bu.`region_org_id`,mp.months FROM `mm_monthly_plan` mp LEFT JOIN `mm_business_unit` bu ON 0=0 LEFT JOIN mm_monthly_plan_basis mpb ON mpb.`prepared_by_unit_org_id` = bu.`business_id` AND mp.`months` = mpb.`months` WHERE bu.`type` = 3 AND mpb.`plan_status` >1 GROUP BY mp.months,bu.`region_org_id`
) xmsl ON xmsl.months = mp.`months` AND bu.`business_id` = xmsl.`region_org_id` INNER JOIN ( SELECT project_id,1 is_manage from s_user_project_rel where userId=${userId} ) temp on temp.project_id=bu.business_id LEFT JOIN (SELECT plan_status,months FROM mm_monthly_plan_basis WHERE prepared_by_level=1) groupstatus ON groupstatus.months=mpb.months
where bu.type=2 and gps.plan_status <> 0 and bu.monthly_weekly_type=1
AND mpb.`months` like #{months}"%"
and mpb.plan_status=#{planStatus}
and bu.business_name like "%"#{businessName}"%"
GROUP BY temp.project_id,mp.months order by mpb.months desc,region_org_id

 select count(*) from mm_monthly_plan_index mm
where mm.prepared_by_unit_org_id =#{praparedByUnitOrgId}
and months=#{months}and mm.prepared_by_unit_type=2

 select count(*) from mm_monthly_plan_index mm
where mm.prepared_by_unit_org_id =#{praparedByUnitOrgId}
and months=#{months}and mm.prepared_by_unit_type=3

