mapper.update:
#修改实际 UPDATE mm_monthly_plan_basis m, (
SELECT is_parking, product_code_sale, version_id, project_id, sum( un_sale_stall_num ) + sum( un_sale_room_num ) room_num_actual, sum( un_sale_stall_price ) + sum( un_sale_room_price ) house_commodity_value_actual FROM v_sman_value_report where project_Id=#{projectId} and end_date=#{kc_date} ) ab SET m.reserve_can_sell_funds = ab.house_commodity_value_actual/10000 , m.reserve_can_sell_set = ab.room_num_actual, m.version_id = ab.version_id, m.is_parking = ab.is_parking, m.product_code_sale = ab.product_code_sale WHERE m.prepared_by_unit_org_id = ab.project_id AND months = #{months};
#修改计划 UPDATE mm_monthly_plan_basis m, (SELECT SUM(IFNULL(house_commodity_value,0))+SUM(IFNULL(not_commodity_value,0)) AS house_commodity_value , SUM(IFNULL(room_num,0)) + SUM(IFNULL(parking_num,0)) AS room_num, a.version_id,a.project_id,a.create_time,a.is_parking,a.product_code_sale FROM v_sman_supply_plan a WHERE a.supply_date BETWEEN #{startDate} AND #{endDate} and (a.supply_date_actual is null or a.supply_date_actual >=#{startDate} ) and a.project_id=#{projectId} GROUP BY a.project_id )ab SET m.new_reserve_funds = ab.house_commodity_value/10000 , m.new_reserve_set = ab.room_num, m.version_id = ab.version_id, m.is_parking = ab.is_parking, m.product_code_sale = ab.product_code_sale WHERE m.prepared_by_unit_org_id = ab.project_id AND months = #{months};
